import React, { useEffect, useState, useCallback } from "react";
import style from "./Daily.module.css";
import SideBar from "../../../components/Sidebar/SideBar";
import { makeStyles } from "@material-ui/core/styles";
import {
  getalljobs,
  getalljobsfiveday,
} from "../../../Redux/Schedule/scheduleAction";
import { connect } from "react-redux";
import { Modal } from "react-bootstrap";
import { Multiselect } from "multiselect-react-dropdown";
import { getAllMovers, updateJob } from "../../../Redux/Job/jobActions";
import { clone, cloneDeep } from "lodash";
import { showMessage } from "../../../Redux/Common/commonActions";
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import { Link } from "react-router-dom";
import Chip from "@material-ui/core/Chip";
import {
  Avatar,
  Button,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Popover,
  Typography,
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCaretLeft,
  faCaretRight,
  faTimes,
} from "@fortawesome/free-solid-svg-icons";

import {
  faInfoCircle,
  faBook,
  faCalendarAlt,
  faUser,
  faClock,
  faBan,
} from "@fortawesome/free-solid-svg-icons";
import DatePicker from "react-horizontal-datepicker";
import parse from "html-react-parser";

import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { htmlToText } from "html-to-text";
// const { htmlToText } = require('html-to-text');

var moverAssignedDate;

const DailySchedule = (props) => {
  const [show, setShow] = useState(false);
  const [newAssignee, setAssignee] = useState("");
  const [jobToUpdate, setJobToUpdate] = useState([]);
  const [allMovers, setAllMovers] = useState();
  const [showIndex, setShowIndex] = useState(null);
  const [today, setToday] = useState(new Date().toString());
  const [startDate, setStartDate] = useState(new Date());
  const [day, setDay] = useState(new Date().getDay());
  const [weekNames, setWeekNames] = useState();
  const [date, setDate] = useState(new Date().toString());
  const [indexDate, setIndexDate] = useState(0);
  const [modalShow, setModalShow] = useState(false);
  const [mover, setMover] = useState("");
  const [test, setTest] = useState(0);
  const { getalljobs, getalljobsfiveday, jobs, movers, newDate } = props;

  useEffect(() => {
    getalljobs({
      date: today,
    });
    getalljobsfiveday({
      // user/get-all-jobs-on-next-five-days
      date: today,
    });
  }, []);

  var specialElementHandlers = {
    "#elementH": function (element, renderer) {
      return true;
    },
  };

  const generatePDF = (e, job) => {
    e.stopPropagation();
    e.nativeEvent.stopImmediatePropagation();
    let dates = job.dates.join(" | ");
    let services = job.services.map((e) => e.name).join(" | ");

    const doc = new jsPDF("p", "pt");
    const date = new Date();
    const columns = [
      { title: "Name", dataKey: "name" },
      { title: "Email", dataKey: "email" },
      { title: "Phone", dataKey: "phone" },
    ];
    const items = [
      {
        name: job.customer.firstName + job.customer.lastName,
        email: job.customer.email,
        phone: job.customer.phone,
      },
    ];

    // text is placed using x, y coordinates
    let imgData =
      "data:image/jpeg;base64,";
    doc.addImage(imgData, "JPEG", 45, 10, 90, 90);
    doc.setFont("times").setFontSize(22).text(420, 40, "JOB DETAILS");
    doc.setFont("times").setFontSize(10).text(400, 70, "Print Date:");
    doc.setFont("times").setFontSize(10).text(490, 70, date.toDateString());
    doc.setFont("times").setFontSize(10).text(400, 85, "Job Id:");
    doc.setFont("times").setFontSize(10).text(490, 85, `${job.jobId}`);
    doc.setFont("times").setFontSize(10).text(400, 100, "Movers Required:");
    doc
      .setFont("times")
      .setFontSize(10)
      .text(490, 100, `${job.assigneeRequired}`);
    doc.setFont("times").setFontSize(10).text(400, 115, "Job Type:");
    doc.setFont("times").setFontSize(10).text(490, 115, job.jobType);

    doc.setFont("times").setFontSize(18).text(50, 200, job.title);

    doc.setFont("times").setFontSize(11).text(50, 235, dates);

    doc.setFont("times").setFontSize(11).text(50, 260, services);

    doc
      .setFont("times")
      .setFontSize(11)
      .text(
        50,
        285,
        job.startTime ? formatAMPM(job.startTime) : "No Time Added"
      );

    doc.autoTable(columns, items, { margin: { top: 310 } });

    doc
      .setFont("times")
      .setFontSize(11)
      .text(50, 375, htmlToText(job.description));

    doc.save(`${job.title}.pdf`);
  };

  const routes = [
    {
      title: "Daily Schedule",
      path: "/schedule",
      icon: <FontAwesomeIcon icon={faClock} />,
    },

    {
      title: "Unavailable",
      path: "/schedule/unavailable",
      icon: <FontAwesomeIcon icon={faBan} />,
    },
    ,
    {
      title: "Movers",
      path: "/schedule/movers",
      icon: <FontAwesomeIcon icon={faUser} />,
    },
  ];

  const handleClose = () => {
    setShow(false);
  };
  const formatAMPM = (startTime) => {
    let date = new Date(startTime);
    var hours = date.getHours();
    var minutes = date.getMinutes();
    var ampm = hours >= 12 ? "pm" : "am";
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? "0" + minutes : minutes;
    var strTime = hours + ":" + minutes + " " + ampm;
    return strTime;
  };
  const updateJobAssignee = (e, job) => {
    if (e) {
      e.stopPropagation();
    }
    var { loggedinUser, showMessage, changeDate } = props;
    let jobToUpdate = cloneDeep(job);
    jobToUpdate.userId = loggedinUser._id;
    jobToUpdate.customerId = jobToUpdate.customer.email;
    delete jobToUpdate.assignee;
    delete jobToUpdate.customer;
    updateJob(jobToUpdate._id, jobToUpdate)
      .then((res) => {
        if (res.data.status == 200) {
          showMessage(res.data.message);
          getalljobs({
            date: today,
          });
          getalljobsfiveday({
            // user/get-all-jobs-on-next-five-days
            date: today,
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const removeAssignee = (e, job, assignee) => {
    e.stopPropagation();
    e.nativeEvent.stopImmediatePropagation();
    e.preventDefault();
    var { loggedinUser, showMessage } = props;
    let jobToUpdate = cloneDeep(job);
    jobToUpdate.userId = loggedinUser._id;
    jobToUpdate.customerId = jobToUpdate.customer.email;
    let assigneesId = jobToUpdate.assignee.map((x) => x._id);
    let assigneeIndex = jobToUpdate.assignee.findIndex(
      (x) => x._id == assignee
    );
    assigneesId.splice(assigneeIndex, 1);
    jobToUpdate.assigneesId = assigneesId;
    delete jobToUpdate.assignee;
    delete jobToUpdate.customer;
    updateJob(jobToUpdate._id, jobToUpdate)
      .then((res) => {
        if (res.data.status == 200) {
          showMessage(res.data.message);
          getalljobs({
            date: today,
          });
          getalljobsfiveday({
            // user/get-all-jobs-on-next-five-days
            date: today,
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onAssigneeSelect = (selectedList, selectedItem) => {
    let job = cloneDeep(jobToUpdate);
    job.assignee = selectedList;
    setJobToUpdate(job);
  };

  const onAssigneeRemove = (selectedList, removedItem) => {
    let job = cloneDeep(jobToUpdate);
    job.assignee = selectedList;
    setJobToUpdate(job);
  };

  const toggleCollapse = (i) => {
    if (i == showIndex) {
      setShowIndex(null);
    } else {
      setShowIndex(i);
    }
  };

  var jobDetailsNavigate = (jobId) => {
    var { history, movers } = props;
    history.push(`/job/detail/${jobId}`);
  };

  var updateJobAssigneeList = (e, moverObj) => {
    e.stopPropagation();
    setModalShow(false);
    moverObj.assigneesId.push(moverObj.moverId);
    moverObj.jobToUpdate.assigneesId = moverObj.assigneesId;
    let job = cloneDeep(moverObj.jobToUpdate);
    updateJobAssignee(e, job);
  };

  const useStyles = makeStyles((theme) => ({
    popover: {
      pointerEvents: "none",
    },
  }));
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = useState(null);
  const [openedPopoverId, setOpenedPopoverId] = useState(null);

  const handlePopoverOpen = (event, id) => {
    event.stopPropagation();
    setOpenedPopoverId(id);
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = (event) => {
    event.stopPropagation();
    setOpenedPopoverId(null);
    setAnchorEl(null);
  };

  const printAllJobs = (e) => {
    for (var job of props.jobs) {
      generatePDF(e, job);
    }
  };

  const Navigate = (e) => {
    setModalShow(false);
    // props.history.push("/schedule/daiily");
  };

  const open = Boolean(anchorEl);

  const onSelectedDay = (d) => {
    getalljobs({
      date: d.toString(),
    });
    getalljobsfiveday({
      date: d.toString(),
    });
    setToday(new Date(d).toString());
    setOpenedPopoverId(null);
    setAnchorEl(null);
  };

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    let moverId = source.droppableId;
    if (!destination) {
      var { showMessage } = props;
      showMessage("Please drop on job item.");
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      var { showMessage } = props;
      showMessage("Please drop on job item.");
      return;
    }

    var jobToUpdate = props.jobs.filter(
      (job) => job.jobId === parseInt(destination.droppableId)
    );

    if (jobToUpdate.length > 0) {
      let index = jobToUpdate[0].assignee.findIndex(
        (x) => x._id == source.droppableId
      );
      if (index != -1) {
        // Already Assigned
        var { showMessage } = props;
        showMessage("Already Assigned");
      } else {
        let assigneesId = jobToUpdate[0].assignee.map((x) => x._id);

        let index = movers.findIndex((x) => x.mover._id == moverId);
        moverAssignedDate = movers[index].mover.jobs.filter((job) =>
          job.dates.some((date) => date === new Date(today).toDateString())
        );
        let moverJobs = moverAssignedDate.length > 0 ? true : false;

        if (moverJobs) {
          setModalShow(true);
          var mover = movers.find((x) => x.mover._id == moverId);
          setMover(mover);
          var newAssigneeObj = {
            moverId,
            assigneesId: assigneesId,
            jobToUpdate: jobToUpdate[0],
          };
          setAssignee(newAssigneeObj);
        } else {
          assigneesId.push(moverId);
          jobToUpdate[0].assigneesId = assigneesId;
          let job = cloneDeep(jobToUpdate[0]);
          updateJobAssignee(null, job);
        }
      }
    }
  };
  console.log(props.jobs);
  return (
    <div className={`${style.scheduleContainer}`}>
      <div className={style.sidebar}>
        <SideBar routes={routes} />
      </div>
      <div className={style.dragContent}>
        <DragDropContext onDragEnd={onDragEnd}>
          <div className={style.jobContent}>
            <div className={style.styleDatePicker}>
              <DatePicker
                endDate={365}
                getSelectedDay={(e) => {
                  onSelectedDay(e);
                }}
                labelFormat={"MMMM yyyy"}
                color={"#181F47"} // #00ADEE
                selectDate={newDate}
              />
            </div>
            <hr className={style.styleLine}></hr>
            <div className={style.moverAssigneeInfo}>
              <div>
                <h6>
                  {`Total Jobs: `} <span>{props.jobs?.length}</span>{" "}
                </h6>
              </div>
              <div>
                <h6>
                  {`Movers Available:`} <span> {props.movers?.length}</span>{" "}
                </h6>
              </div>
              <div>
                <h6>
                  Movers Required:{" "}
                  {props.jobs?.length > 0 ? (
                    props.jobs.reduce(
                      (sum, currentValue) =>
                        sum + currentValue.assigneeRequired,
                      0
                    )
                  ) : (
                    <span>0</span>
                  )}
                </h6>
              </div>
              <div>
                <Button className={style.button} onClick={printAllJobs}>
                  <i className="fa fa-print"></i>
                  Print All
                </Button>
              </div>
            </div>
            <hr className={style.styleLine}></hr>
            {props.jobs?.length > 0 && (
              <div className={style.jobListHeader}>
                <div>Title</div>
                <div>Movers Req.</div>
                <div>Time</div>
                <div>Assignee</div>
                <div>Action</div>
              </div>
            )}
            {props.jobs && props.jobs.length > 0 ? (
              props.jobs.map((list, i) => {
                return (
                  <Droppable
                    droppableId={list.jobId.toString()}
                    type="TASK"
                    key={i}
                  >
                    {(provided, snapshot) => (
                      <div
                        id="accordion"
                        key={i}
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                      >
                        <div
                          className={`card-header ${style.cardHeader}`}
                          aria-expanded="true"
                          data-toggle="collapse"
                          data-target={`#collapse${i}`}
                          aria-controls="collapse"
                          id="headingOne"
                          onClick={() => toggleCollapse(i)}
                          onDoubleClick={() => jobDetailsNavigate(list._id)}
                        >
                          <div>{list.title}</div>

                          <div>
                            {list.assigneeRequired}
                            <span className={style.hideJobId}>
                              {list.jobId}
                            </span>
                          </div>

                          <div>
                            {list.startTime
                              ? formatAMPM(list.startTime)
                              : "N/A"}
                          </div>
                          <div>
                            {list.assignee.length > 0 ? (
                              <div>
                                {list.assignee.map((assignee, i) => (
                                  <div key={i} className={style.assignee}>
                                    <div className={style.assigneeName}>
                                      {/* <span> &#42;</span> */}
                                      {assignee.name}
                                    </div>

                                    <div className={style.closeIcon}>
                                      <FontAwesomeIcon
                                        onClick={(e) =>
                                          removeAssignee(e, list, assignee._id)
                                        }
                                        icon={faTimes}
                                        size="1x"
                                      />
                                    </div>
                                  </div>
                                ))}
                              </div>
                            ) : (
                              <div>N/A</div>
                            )}
                          </div>

                          <Modal
                            show={modalShow}
                            size="lg"
                            aria-labelledby="contained-modal-title-vcenter"
                            centered
                            onHide={(e) => Navigate(e)}
                          >
                            <Modal.Header closeButton>
                              <Modal.Title id="contained-modal-title-vcenter">
                                Confirmation
                              </Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                              {mover && (
                                <h5>
                                  {" "}
                                  {mover.mover.name}{" "}
                                  <span className={style.styleText}>
                                    has been assigned to these jobs:
                                  </span>{" "}
                                </h5>
                              )}

                              {mover &&
                                moverAssignedDate.map((job, i) => (
                                  <div key={i} className={style.styleModalBody}>
                                    <a
                                      className={style.styleLink}
                                      onClick={() =>
                                        window.open(
                                          `/job/detail/${job._id}`,
                                          "_blank"
                                        )
                                      }
                                    >
                                      &#42;
                                      {job.title}
                                    </a>{" "}
                                    <Chip
                                      label={
                                        job.startTime
                                          ? formatAMPM(job.startTime)
                                          : "N/A"
                                      }
                                      clickable
                                      color="primary"
                                      variant="outlined"
                                    ></Chip>
                                  </div>
                                ))}
                            </Modal.Body>
                            <Modal.Footer>
                              <div className={style.modalBtns}>
                                <Button
                                  className={style.button}
                                  onClick={(e) =>
                                    updateJobAssigneeList(e, newAssignee)
                                  }
                                >
                                  Confirm
                                </Button>
                                <Button
                                  className={style.button}
                                  onClick={(e) => {
                                    Navigate(e);
                                  }}
                                >
                                  Close
                                </Button>
                              </div>
                            </Modal.Footer>
                          </Modal>
                          <div>
                            <Button onClick={(e) => generatePDF(e, list)}>
                              <i className="fa fa-print"></i>
                              Print
                            </Button>
                          </div>
                        </div>
                        <div
                          id={`collapse${i}`}
                          className="collapse"
                          aria-labelledby="headingOne"
                          data-parent="#accordion"
                        >
                          <div className="card-body">
                            <h4>Job Details</h4>
                            <div className={style.jobDetailHeader}>
                              <div>
                                <h6>Job Id</h6>
                              </div>
                              <div>
                                <h6>Job Title</h6>
                              </div>
                              <div>
                                <h6>Job Type</h6>
                              </div>
                              <div>
                                <h6>Status</h6>
                              </div>
                            </div>

                            <div className={style.jobDetailList}>
                              <div>{list.jobId}</div>
                              <div>{list.title}</div>
                              <div>{list.jobType}</div>
                              <div>
                                <Chip
                                  label={list.status}
                                  clickable
                                  size="small"
                                  color="primary"
                                  variant="outlined"
                                ></Chip>
                              </div>
                            </div>
                            <div className={style.jobDescriptionHeader}>
                              <h6>Job Description</h6>
                            </div>
                            <div className={style.jobDescription}>
                              <p>{parse(list.description)}</p>
                            </div>
                            <hr />
                            <h6>Customer Details</h6>
                            <div className={style.customerDetailHeader}>
                              <div>
                                <h6>Name</h6>
                              </div>
                              <div>
                                <h6>Email</h6>
                              </div>
                              <div>
                                <h6>Phone</h6>
                              </div>
                            </div>
                            <div className={style.customerDetailList}>
                              <div>
                                {list.customer.firstName}{" "}
                                {list.customer.lastName}
                              </div>
                              <div>{list.customer.email}</div>
                              <div>{list.customer.phone}</div>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </Droppable>
                );
              })
            ) : (
              <div className="text-center">
                <img src="/images/no-data-found.png" />
              </div>
            )}
          </div>

          <div className={`${style.movers}  ${style.mov}`} id="mov">
            <div className={` ${style.scroll}`}>
              <h4 className={`  ${style.movehead}`}>Movers</h4>
              <div className={`${style.scrollBar}`}>
                <div className={`  ${style.scrollContent}`}>
                  {movers &&
                    movers.map((list, i) => {
                      return (
                        <div key={i}>
                          <Droppable
                            droppableId={list.mover._id.toString()}
                            type="TASK"
                          >
                            {(provided, snapshot) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                              >
                                <Draggable
                                  draggableId={list.mover._id.toString()}
                                  index={i}
                                >
                                  {(provided, snapshot) => (
                                    <div
                                      {...provided.draggableProps}
                                      {...provided.dragHandleProps}
                                      ref={provided.innerRef}
                                    >
                                      {" "}
                                      <h6 className={style.moverHeader} key={i}>
                                        <label>{i + 1}.</label>{" "}
                                        {list.mover.name}{" "}
                                        <span className={style.hideAssigneeId}>
                                          {list.mover._id}
                                        </span>
                                      </h6>
                                    </div>
                                  )}
                                </Draggable>
                              </div>
                            )}
                          </Droppable>
                        </div>
                      );
                    })}
                </div>
              </div>
            </div>
          </div>
        </DragDropContext>
      </div>

      <Modal show={show} onHide={handleClose} animation={false} centered>
        <Modal.Header closeButton>
          <Modal.Title>Assignees</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div>
              <Multiselect
                selectedValues={jobToUpdate?.assignee}
                options={allMovers} // Options to display in the dropdown
                onSelect={onAssigneeSelect} // Function will trigger on select event
                onRemove={onAssigneeRemove} // Function will trigger on remove event
                displayValue="name" // Property name to display in the dropdown options
              />
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleClose} name="Close"></Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};
var mapStateToProps = (state) => ({
  jobs: state.schedule.jobList,
  movers: state.schedule.moverList,
  loggedinUser: state.users.user,
  newDate: state.common.scheduleDate,
});

var actions = {
  getalljobs,
  getalljobsfiveday,
  showMessage,
};
export default connect(mapStateToProps, actions)(DailySchedule);
